package com.example.myapplication;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.BaseAdapter;
import android.widget.GridView;
import android.widget.ImageButton;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

import com.bumptech.glide.Glide;

import java.util.ArrayList;


public class GridViewTest extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_grid_view_test);
        Movie movie1=new Movie("Home","data:image/jpeg;base64,");
        Movie movie2=new Movie("Rabbit School","data:image/jpeg;base64,");
        Movie movie3=new Movie("Moana","data:image/jpeg;base64,");
        Movie movie4=new Movie("Mary And The Witche Flower","data:image/jpeg;base64,");
        Movie movie5=new Movie("Tangled","data:image/jpeg;base64,");
        ArrayList<Movie> arrayList=new ArrayList<Movie>();
        arrayList.add(movie1);
        arrayList.add(movie2);
        arrayList.add(movie3);
        arrayList.add(movie4);
        arrayList.add(movie5);
        GridView gridView=findViewById(R.id.gridview);
        GridAdapter gridAdapter=new GridAdapter(arrayList);
        gridView.setAdapter(gridAdapter);

    }
    public class GridAdapter extends BaseAdapter
    {
        ArrayList<Movie> movies=new ArrayList<Movie>();

        public GridAdapter(ArrayList<Movie> movies) {
            this.movies = movies;
        }

        @Override
        public int getCount() {
            return movies.size();
        }

        @Override
        public Object getItem(int position) {
            return movies.get(position);
        }

        @Override
        public long getItemId(int position) {
            return position;
        }

        @Override
        public View getView(int position, View convertView, ViewGroup parent) {
            LayoutInflater inflater=getLayoutInflater();
            View myview=inflater.inflate(R.layout.movieitemgrid,null);
            TextView txtname=myview.findViewById(R.id.txtgridname);
            txtname.setText(movies.get(position).name);
            ImageButton imagegrid=myview.findViewById(R.id.imagegrid);
            Glide.with(getApplicationContext())
                    .load(movies.get(position).movieLink)
                    .into(imagegrid);

            return myview;
        }
    }

}
